  IN p_phone VARCHAR, 
  IN p_street1 TEXT, 
  IN p_street2 TEXT, 
  IN p_city VARCHAR, 
  IN p_state CHAR, 
  IN p_zip_code VARCHAR, 
  IN p_terms_accepted TIMESTAMP, 
