    );
    CALL pareto.i_logs(v_response.status, v_response.message, c_service_name, p_created_by, v_metadata);
    RETURN v_response;
  END IF;
  
  -- ------------------------------------------------------
  -- Persist
  -- ------------------------------------------------------
 
  INSERT INTO pareto.user (
    email, 
    last_name, 
    first_name, 
    phone, 
    street1, 
    street2, 
    city, 
    state, 
    zip_code, 
    terms_accepted, 
    created_by,
    updated_by
  )
  VALUES (
    p_email, 
    p_last_name, 
    p_first_name, 
    p_phone, 
    p_street1, 
    p_street2, 
    p_city, 
